<?function getSnippett($filename)	{	$s = file("content/snippets/$filename");	for ($a=0;$a<count($s);$a++)		{		$s[$a] = chop($s[$a]);		}	$s = join($s,"<br>");	return $s;	}function h($key)	{	?><span class="link" onmouseup="showhelp('<?echo $key?>')"><u><i><?echo $key?></i></u></span><?	}function l($key)	{	?><span class="link" onmouseup="showlink('<?echo $key?>')"><u><i><?echo $key?></i></u></span><?	}function p($key)	{	?><span class="link" onmouseup="showpopup('<?echo $key?>')"><u><i><?echo $key?></i></u></span><?	}function CON($key)	{	global $editmode;	$content = "&nbsp";	$rs = SQL("SELECT * FROM content WHERE tag = '" . $key . "'");	if ($t = mysql_fetch_array($rs))		{		$content = $t["content"];		}			if ($editmode == "EDIT")		{		?>		<div onclick="popedit(this,'<?echo $key?>')" style='border-style:solid;border-width:1pt;border-color:red;cursor:hand;'>		<?		}	else if ($editmode == "EDITS")		{		?>		<div onclick="popedits(this,'<?echo $key?>')" style='border-style:solid;border-width:1pt;border-color:red;cursor:hand;'>		<?		}	else		{		echo "<div id='" . $key . "' oncontextmenu=\"CONContext('" . $key . "')\">";		}	echo $content;	?>	</div>	<?	}function CON_NODIV($key)	{	global $editmode;	$content = "&nbsp";	$rs = SQL("SELECT * FROM content WHERE tag = '" . $key . "'");	if ($t = mysql_fetch_array($rs))		{		$content = $t["content"];		}	echo $content;	}?>